import { Button } from "@chakra-ui/button";
import { useDisclosure } from "@chakra-ui/hooks";
import { Input } from "@chakra-ui/input";
import { Box, Flex, Heading, Link, Stack } from "@chakra-ui/layout";
import { ChangeEvent, useState, VFC } from "react";
import { SubmitHandler, useForm } from "react-hook-form";
import { useLoginUser } from "../../hooks/useLoginUser";
import { SignupModal } from "../molecules/SignupModal";

export const Login: VFC = () => {
    const {onOpen, isOpen, onClose} = useDisclosure();
    const {login} = useLoginUser();
    // const {register, formState: { errors }, handleSubmit} = useForm<LoginInputType>();
    // const onSubmit: SubmitHandler<LoginInputType> = data => console.log(data);
    // //login(data.email, data.password);
    // interface LoginInputType {
    //     email: string;
    //     password: string;
    // }
    const {register, formState: { errors }, handleSubmit} = useForm<IFormInput>();
    const onSubmit: SubmitHandler<IFormInput> = data => console.log(data);
    interface IFormInput {
        firstName: string;
        lastName: string;
    }


    return (
        <>
            <Flex align="center" justify="center" h="100vh">
                <Box backgroundColor="gray.100" borderRadius="10px" boxShadow="md" textAlign="center" p={4}>
                    <Heading>Login</Heading>
                    <Box mt={6} p={4}>
                        <Stack spacing={4}>
                            <Box w="80%">
                            <form onSubmit={handleSubmit(onSubmit)}>
                                    <Input border="1px" borderColor="gray.300" placeholder="E-Mail" {...register("firstName", { required: true })} />
                                    {errors.firstName && "First name is required"}
                                    <Input type="password" border="1px" borderColor="gray.300" placeholder="Password" {...register("lastName", { required: true })} />
                                    {errors.lastName && "Last name is required"}
                                    <Input type="submit" backgroundColor="teal.400" _hover={{opacity: "0.7"}} w="100%" fontFamily="Georgia" fontWeight="bold" value="ログイン"/>
                            </form>
                                {/* <form onSubmit={handleSubmit(onSubmit)}>
                                    <Input border="1px" borderColor="gray.300" placeholder="E-Mail" {...register("email")}/>
                                    {errors.email && "Eメールを入力してください"}
                                    <Input type="password" border="1px" borderColor="gray.300" placeholder="Password" {...register("password")}/>
                                    {errors.password && "パスワードを入力してください"}
                                    <Button backgroundColor="teal.400" _hover={{opacity: "0.7"}} w="100%">ログイン</Button>
                                </form> */}
                            </Box>
                        </Stack>
                        <Box mt={4}>
                            <Link fontWeight="bold" color="gray.500" onClick={onOpen}>ユーザー登録</Link>
                        </Box>
                    </Box>
                </Box>
            </Flex>
            <SignupModal onClose={onClose} isOpen={isOpen}/>
        </>
    );
};
