import { Button } from "@chakra-ui/button";
import { Image } from "@chakra-ui/image";
import { Input } from "@chakra-ui/input";
import { Box, Flex, Heading, Stack, Text } from "@chakra-ui/layout";
import { useEffect, VFC } from "react";
import { useGetMyTweets } from "../../hooks/useGetMyTweets";

export const Home: VFC = () => {
    const {getMyTweetState, myTweets, getMyTweets} = useGetMyTweets();

    useEffect(() => {
        getMyTweets();
    }, []);

    return (
        <>
            <Box backgroundColor="teal.400" display={{base: "none", sm: "block"}} ml={60}>
                <Box ml={2}>
                    <Heading fontSize="3xl" p={2}>Home</Heading>
                </Box>
            </Box>
            <Box ml={{sm: "60"}}>
                <Box w={{base: "80%", sm: "60%"}} ml={{base: "auto", sm: "16"}} mt={14}>
                    <Input size="lg" placeholder="今どうしている？"/>
                    <Box textAlign="right">
                        <Button backgroundColor="teal.300" borderRadius="999px" mt={4}>ツイートする</Button>
                    </Box>
                </Box>
                <Box display="block" ml={16} mt={14}>
                    {myTweets.map((myTweet) => (
                        <Stack>
                            <Flex>
                                <Box>
                                    <Image boxSize="60px" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                                </Box>
                                <Box borderBottom="1px" borderColor="gray.200">
                                    <Text fontWeight="bold">{myTweet.username}</Text>
                                    <Text>{myTweet.time}</Text>
                                    <Text>{myTweet.tweet}</Text>
                                </Box>
                            </Flex>
                        </Stack>
                    ))}
                </Box>
            </Box>
        </>
    );
};
