import { Flex, Spacer, Stack, Text } from "@chakra-ui/layout";
import { Modal, ModalBody, ModalCloseButton, ModalContent, ModalHeader, ModalOverlay } from "@chakra-ui/modal";
import { Textarea } from "@chakra-ui/textarea";
import { useEffect, useState, VFC } from "react";
import { TweetType } from "../../types/tweetType";
import { FaThumbsUp, FaRegCommentAlt } from "react-icons/fa";
import { Button } from "@chakra-ui/button";

type Props = {
    tweetCount?: Number;
    myTweets: Array<TweetType>;
    onClose: () => void;
    isOpen: boolean;
};

export const MyTweetModal: VFC<Props> = (props) => {
    const {tweetCount, myTweets, onClose, isOpen} = props;
    const [targetTweet, setTargetTweet] = useState<TweetType>();

    useEffect(() => {
        const findTweet = myTweets.find((myTweet) => tweetCount === myTweet.id);
        setTargetTweet(findTweet);
    });
    return (
        <Modal onClose={onClose} isOpen={isOpen}>
            <ModalOverlay>
                <ModalContent>
                    <ModalHeader>Tweet</ModalHeader>
                    <ModalBody>
                        <Stack>
                            <Textarea value={targetTweet?.tweet}/>
                            <Flex p={2}>
                                <Flex align="center">
                                    <FaThumbsUp size={16}/>
                                    <Text ml={2}>{targetTweet?.tweetLikeCount}</Text>
                                </Flex>
                                <Flex align="center" ml={8}>
                                    <FaRegCommentAlt size={16}/>
                                    <Text>{targetTweet?.tweetCommentCount}</Text>
                                </Flex>
                                <Spacer />
                                <Button>更新</Button>
                                <Button>削除</Button>
                            </Flex>
                        </Stack>
                    </ModalBody>
                    <ModalCloseButton />
                </ModalContent>
            </ModalOverlay>
        </Modal>
    );
};
