import { Image } from "@chakra-ui/image";
import { Box, Link } from "@chakra-ui/layout";
import { Drawer, DrawerBody, DrawerCloseButton, DrawerContent, DrawerOverlay } from "@chakra-ui/modal";
import { VFC } from "react";

type Props = {
    onClose: () => void;
    isOpen: boolean;
}

export const MenuDrawer: VFC<Props> = (props) => {
    const {onClose, isOpen} = props;

    return (
        <Drawer placement="left" onClose={onClose} isOpen={isOpen} variant="outline">
            <DrawerOverlay>
                <DrawerContent>
                    <DrawerBody>
                        <Box p={4} borderColor="gray.300" textAlign="center">
                            <Image boxSize="150px" borderRadius="full" m="auto" src="https://source.unsplash.com/random" alt=""/>
                            <Box>
                            <Link fontSize="lg" fontWeight="bold" mt={10}>Text</Link>
                            </Box>
                        </Box>
                    </DrawerBody>
                    <DrawerCloseButton />
                </DrawerContent>
            </DrawerOverlay>
        </Drawer>
    );
};
