import { Button } from "@chakra-ui/button";
import { Image } from "@chakra-ui/image";
import { Input } from "@chakra-ui/input";
import { Box } from "@chakra-ui/layout";
import { Modal, ModalBody, ModalCloseButton, ModalContent, ModalHeader, ModalOverlay } from "@chakra-ui/modal";
import { ChangeEvent, useState, VFC } from "react";
import { useChangeImage } from "../../hooks/useChangeImage";

type Props = {
    onClose: () => void;
    isOpen: boolean;
};

export const ImageUploadModal: VFC<Props> = (props) => {
    const {onClose, isOpen} = props;
    const [profileImage, setProfileImage] = useState<string>();

    const getBase64 = (file: File) => {
        let reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
            let baseURL = reader.result as string;
            setProfileImage(baseURL);
        };
    }
    
    const onChangeInputImage = (event: ChangeEvent<HTMLInputElement>) => {
        let image = event.target.files![0];
        getBase64(image);
        console.log(event.target.files![0]);
    }

    const onClickImageReset = () => {
        setProfileImage("");
    };

    return (
        <Modal onClose={onClose} isOpen={isOpen}>
            <ModalOverlay>
                <ModalContent>
                    <ModalHeader>画像を変更する</ModalHeader>
                    <ModalBody>
                        <Input type="file" accept="image/*" border="none" onChange={onChangeInputImage}/>
                        <Box w="100%" p={4}>
                            <Image src={profileImage}/>
                        </Box>
                        <Box mb={4}>
                            <Button border="1px" borderColor="gray.600" backgroundColor="gray.50" size="sm" borderRadius="3" ml={4} onClick={onClickImageReset}>変更</Button>
                            <Button border="1px" borderColor="gray.600" backgroundColor="gray.50" size="sm" borderRadius="3" ml={4} onClick={onClickImageReset}>リセット</Button>
                        </Box>
                    </ModalBody>
                    <ModalCloseButton />
                </ModalContent>
            </ModalOverlay>
        </Modal>
    );
};
