import { Image } from "@chakra-ui/image";
import { memo, VFC } from "react";

type Props = {
    size: string;
    image?: string;
};

export const ProfileImage: VFC<Props> = memo((props) => {
    const {size, image} = props;
    let profileImage;
    console.log(image);
    if(image === "null"){
        profileImage = `${process.env.PUBLIC_URL}/user_icon.png`
    }else{
        profileImage = image;
    };

    return (
        <Image boxSize={size} borderRadius="full" src={profileImage as string} alt="" />
    );
});
