import axios from "axios";
import { useState } from "react";
import { useCurrentLoginUser } from "./useCurrentLoginUser";

export const useChangeImage = () => {
    const [changeImageState, setChangeImageState] = useState(false);
    const {loginUser} = useCurrentLoginUser();
    const changeImage = (image?: string) => {
        setChangeImageState(false);
        const iconPram = new FormData();
        if(!image) return;
        iconPram.append("userIcon", image);
        axios.post("http://localhost:8080/api/login/changeImage", iconPram, {headers: {"content-type": "multipart/form-data", Authorization: loginUser.token as string}})
    };

    return {changeImageState, changeImage};
};
