import axios from "axios";
import { useState } from "react";
import { useMessage } from "./useMessage";

export const useCreateUser = () => {
    const [createUserState, setCreateUserState] = useState(false);
    const {showMessage} = useMessage();
    const createUser = (email: string, password: string, username: string) => {
        setCreateUserState(true);
        axios.post("http://localhost:8080/api/login/signup", {email: email, password: password, username: username})
        .then((res) => showMessage({title: res.data as string, status: "success"}))
        .catch((res) => {
            showMessage({title: res.data as string, status: "error"})
            console.log(res);
        });
    };

    return {createUser, createUserState};
};
