import { Button } from "@chakra-ui/button";
import { useDisclosure } from "@chakra-ui/hooks";
import { Image } from "@chakra-ui/image";
import { Input } from "@chakra-ui/input";
import { Box, Flex, Heading, Stack, Text } from "@chakra-ui/layout";
import { ChangeEvent, useEffect, useState, VFC } from "react";
import { useGetMyTweets } from "../../hooks/useGetMyTweets";
import { FaRegThumbsUp, FaRegCommentAlt, FaTelegramPlane } from "react-icons/fa";
import { useInsertTweet } from "../../hooks/useInsertTweet";
import { MyTweetModal } from "../molecules/MyTweetModal";

export const Home: VFC = () => {
    const {onOpen, onClose, isOpen} = useDisclosure();
    const {myTweets, getMyTweets} = useGetMyTweets();
    const {insertTweetState, insertTweet} = useInsertTweet();
    const [tweet, setTweet] = useState("");
    const [tweetCount, setTweetCount] = useState<Number>();

    const onClickSendCount = (count?: Number) => {
        setTweetCount(count);
        onOpen();
    };
    const onChangeInsertTweet = (event: ChangeEvent<HTMLInputElement>) => setTweet(event.target.value);
    const onClickInsertTweet = () => {
        insertTweet(tweet);
        setTweet("");
    };

    useEffect(() => {
        getMyTweets();
    }, [insertTweetState]);

    return (
        <>
            <Box backgroundColor="teal.600" display={{base: "none", sm: "block"}} position="fixed" w="100%">
                <Box ml={2}>
                    <Heading fontSize="3xl" p={2} color="whiteAlpha.800">Home</Heading>
                </Box>
            </Box>
            <Box ml={{sm: "56"}} mt={{base: "6", lg: "24"}}>
                <Box w={{base: "80%", md: "55%", xl: "200%"}} mt={{base: "12", sm: "24"}} ml={{base: "8", sm: "6"}}>
                    <Input borderColor="gray.400" onChange={onChangeInsertTweet}/>
                    <Box mt={1} textAlign="right">
                        <Button borderRadius="full" backgroundColor="teal.500" color="whiteAlpha.800" onClick={onClickInsertTweet}>ツイートする</Button>
                    </Box>
                </Box>
                <Box display="block" ml={10} mt={14}>
                    <Stack spacing={6}>
                        {myTweets.map((myTweet) => (
                            <Flex _hover={{cursor: "pointer"}} onClick={() => onClickSendCount(myTweet.id)}>
                                <Box>
                                    <Image boxSize="50px" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                                </Box>
                                <Box w="100%" p={2}>
                                    <Text fontWeight="bold">{myTweet.username}</Text>
                                    <Text>{myTweet.time}</Text>
                                    <Text mt={2} mb={4}>{myTweet.tweet}</Text>
                                    <Flex>
                                        <Flex align="center">
                                            <FaRegThumbsUp size={18}/>
                                            <Text ml={2}>{myTweet.tweetLikeCount}</Text>
                                        </Flex>
                                        <Flex align="center" ml={8}>
                                            <FaRegCommentAlt size={18}/>
                                            <Text ml={2}>{myTweet.tweetCommentCount}</Text>
                                        </Flex>
                                    </Flex>
                                </Box>
                            </Flex>
                        ))}
                    </Stack>
                </Box>
            </Box>

            <MyTweetModal tweetCount={tweetCount} myTweets={myTweets} onClose={onClose} isOpen={isOpen}/>
        </>
    );
};
