import { Flex } from "@chakra-ui/react";
import { Heading } from "@chakra-ui/layout";
import { VFC } from "react";

export const Header: VFC = () => {
    return (
        <>
            <Flex align="center" justify="space-between" p={2} backgroundColor="teal.100" color="gray.900" display={{base: "flex", sm: "none"}}>
                <Flex as="a">
                    <Heading>Tweet App</Heading>
                </Flex>
            </Flex>
        </>
    );
};
