import { Image } from "@chakra-ui/image";
import { Box, Flex, Stack, Text } from "@chakra-ui/layout";
import { Drawer, DrawerBody, DrawerCloseButton, DrawerContent, DrawerOverlay } from "@chakra-ui/modal";
import { VFC } from "react";
import { FaWrench, FaHome, FaWeixin } from "react-icons/fa";

type Props = {
    onClose: () => void;
    isOpen: boolean;
    onClickGoHome: () => void;
    onClickGoTweet: () => void;
    onClickGoSetting: () => void;
}

export const MenuDrawer: VFC<Props> = (props) => {
    const {onClose, isOpen, onClickGoHome, onClickGoTweet, onClickGoSetting} = props;

    return (
        <Drawer placement="left" onClose={onClose} isOpen={isOpen} variant="outline">
            <DrawerOverlay>
                <DrawerContent>
                    <DrawerBody>
                        <Box p={4} pb={8} mt={6} borderBottom="4px" borderColor="gray.300">
                            <Image boxSize="120px" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                            <Box mt={4} ml={2}>
                                <Text fontSize="2xl" fontWeight="bold">Test User</Text>
                            </Box>
                        </Box>
                        <Box mt={6} p={4} fontSize="2xl" fontWeight="bold">
                            <Stack>
                                <Flex align="center">
                                    <FaHome size={24} />
                                    <Text ml={2} onClick={() => {
                                        onClickGoHome();
                                        onClose();
                                    }}>Home</Text>
                                </Flex>
                                <Flex align="center">
                                    <FaWeixin size={24} />
                                    <Text ml={2} onClick={() => {
                                        onClickGoTweet();
                                        onClose();
                                    }}>Tweet</Text>
                                </Flex>
                                <Flex align="center">
                                    <FaWrench size={24}/>
                                    <Text ml={2} onClick={() => {
                                        onClickGoSetting();
                                        onClose();
                                    }}>Setting</Text>
                                </Flex>
                            </Stack>
                        </Box>
                    </DrawerBody>
                    <DrawerCloseButton />
                </DrawerContent>
            </DrawerOverlay>
        </Drawer>
    );
};
