import { Box, Flex, Spacer, Stack, Text } from "@chakra-ui/layout";
import { Modal, ModalBody, ModalCloseButton, ModalContent, ModalHeader, ModalOverlay } from "@chakra-ui/modal";
import { Textarea } from "@chakra-ui/textarea";
import { useEffect, useState, VFC } from "react";
import { TweetType } from "../../types/tweetType";
import { FaThumbsUp, FaRegCommentAlt } from "react-icons/fa";
import { Button } from "@chakra-ui/button";
import { Image } from "@chakra-ui/image";

type Props = {
    tweetCount?: Number;
    myTweets: Array<TweetType>;
    onClose: () => void;
    isOpen: boolean;
};

export const MyTweetModal: VFC<Props> = (props) => {
    const {tweetCount, myTweets, onClose, isOpen} = props;
    const [targetTweet, setTargetTweet] = useState<TweetType>();

    useEffect(() => {
        const findTweet = myTweets.find((myTweet) => tweetCount === myTweet.id);
        setTargetTweet(findTweet);
        console.log(targetTweet)
    });
    return (
        <Modal onClose={onClose} isOpen={isOpen}>
            <ModalOverlay>
                <ModalContent>
                    <ModalHeader>Tweet</ModalHeader>
                    <ModalBody>
                        <Stack>
                            <Textarea value={targetTweet?.tweet}/>
                            <Flex p={2}>
                                <Flex align="center">
                                    <FaThumbsUp size={16}/>
                                    <Text ml={2}>{targetTweet?.tweetLikeCount}</Text>
                                </Flex>
                                <Flex align="center" ml={8}>
                                    <FaRegCommentAlt size={16}/>
                                    <Text ml={2}>{targetTweet?.tweetCommentCount}</Text>
                                </Flex>
                                <Spacer />
                                <Button w="20%" backgroundColor="blue.300" _hover={{opacity: "0.8"}}>更新</Button>
                                <Button w="20%" backgroundColor="red.300" ml={1} _hover={{opacity: "0.8"}}>削除</Button>
                            </Flex>
                        </Stack>
                        <Stack spacing={4} mt={14}>
                            {targetTweet?.tweetCommentResource?.map((commentResource) => (
                                <Flex>
                                    <Box>
                                        <Image boxSize="40px" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                                    </Box>
                                    <Box>
                                        <Text fontSize="md" fontWeight="bold">{commentResource.username}</Text>
                                        <Text fontSize="sm">{commentResource.time}</Text>
                                        <Text fontSize="sm">{commentResource.comment}</Text>
                                    </Box>
                                </Flex>
                            ))}
                        </Stack>
                    </ModalBody>
                    <ModalCloseButton />
                </ModalContent>
            </ModalOverlay>
        </Modal>
    );
};
