import { Button } from "@chakra-ui/button";
import { Image } from "@chakra-ui/image";
import { Box, Flex, Spacer, Stack, Text } from "@chakra-ui/layout";
import { Modal, ModalBody, ModalCloseButton, ModalContent, ModalHeader, ModalOverlay } from "@chakra-ui/modal";
import { Textarea } from "@chakra-ui/textarea";
import { ChangeEvent, useEffect, useState, VFC } from "react";
import { useCurrentLoginUser } from "../../hooks/useCurrentLoginUser";
import { TweetType } from "../../types/tweetType";
import { FaRegThumbsUp, FaThumbsUp, FaRegCommentAlt } from "react-icons/fa";

type Props = {
    tweetId?: Number;
    allTweets: Array<TweetType>;
    onClose: () => void;
    isOpen: boolean;
    insertTweetLike: (tweetId?: Number) => void;
    deleteTweetLike: (tweetId?: Number) => void;
    insertTweetComment: (tweetId?: Number, comment?: string) => void;
};

export const AllTweetModal: VFC<Props> = (props) => {
    const {tweetId, allTweets, onClose, isOpen, insertTweetLike, deleteTweetLike, insertTweetComment} = props;
    const [targetTweet, setTargetTweet] = useState<TweetType>();
    const [comment, setComment] = useState("");
    const {loginUser} = useCurrentLoginUser();

    const isLike = () => {
        let isLikeFrag = false;
        for(let i = 0; i < targetTweet?.tweetLikeList?.length!; ++ i){
            if(loginUser.email === targetTweet?.tweetLikeList![i]){
                isLikeFrag = true;
                break;
            }
        };
        return isLikeFrag;
    };
    const onClickFollow = () => {
        if(isLike()){
            deleteTweetLike(tweetId);
        }else{
            insertTweetLike(tweetId);
        };
    };
    const onChangeTweetComment = (event: ChangeEvent<HTMLTextAreaElement>) => setComment(event.target.value);
    const onClickTweetComment = () => {
        insertTweetComment(tweetId, comment);
        setComment("");
    };

    useEffect(() => {
        const findTweet = allTweets.find((allTweet) => tweetId === allTweet.id);
        setTargetTweet(findTweet);
    });

    return (
        <Modal onClose={onClose} isOpen={isOpen}>
            <ModalOverlay>
                <ModalContent>
                    <ModalHeader>Tweet</ModalHeader>
                    <ModalBody>
                        <Box>
                            <Flex>
                                <Flex>
                                    <Box mr={2}>
                                        <Image boxSize="60px" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                                    </Box>
                                    <Box>
                                        <Text fontSize="lg" fontWeight="bold">{targetTweet?.username}</Text>
                                        <Text fontSize="sm">{targetTweet?.time}</Text>
                                        <Text fontSize="lg" my={3}>{targetTweet?.tweet}</Text>
                                    </Box>
                                </Flex>
                                <Spacer />
                                <Box>
                                    <Button size="sm" fontSize="xs" backgroundColor="blue.200" borderRadius="full" _hover={{opacity: "0.7"}}>フォロー</Button>
                                </Box>
                            </Flex>
                            <Flex ml={4}>
                                <Flex align="center" onClick={onClickFollow}>
                                    <Box>
                                        {isLike() ? (
                                            <FaThumbsUp size={16}/>
                                        ) : (
                                            <FaRegThumbsUp size={16} />
                                        )}
                                    </Box>
                                    <Text ml={2}>{targetTweet?.tweetLikeCount}</Text>
                                </Flex>
                                <Flex align="center" ml={10}>
                                    <Box>
                                        <FaRegCommentAlt size={16}/>
                                    </Box>
                                    <Text ml={2}>{targetTweet?.tweetCommentCount}</Text>
                                </Flex>
                            </Flex>
                            <Flex align="end" my={6}>
                                <Textarea borderColor="gray.300" placeholder="コメントする" value={comment} onChange={onChangeTweetComment}/>
                                <Button backgroundColor="gray.400" ml={2} _hover={{opacity: "0.7"}} onClick={onClickTweetComment}>コメント</Button>
                            </Flex>
                            {targetTweet?.tweetCommentResource?.length === 0 ? (
                                <>
                                </>
                            ) : (
                                <Stack spacing={4} mt={10} mb={2}>
                                {targetTweet?.tweetCommentResource?.map((commentResource) => (
                                    <Flex p={1}>
                                        <Box>
                                            <Image boxSize="40px" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                                        </Box>
                                        <Box ml={2}>
                                            <Text fontSize="md" fontWeight="bold">{commentResource.username}</Text>
                                            <Text fontSize="xs">{commentResource.time}</Text>
                                            <Text fontSize="md">{commentResource.comment}</Text>
                                        </Box>
                                    </Flex>
                                ))}
                                </Stack>
                            )}
                        </Box>
                    </ModalBody>
                    <ModalCloseButton />
                </ModalContent>
            </ModalOverlay>
        </Modal>
    );
};
