import { Button } from "@chakra-ui/button";
import { Input } from "@chakra-ui/input";
import { Box, Flex, Heading, Link, Stack } from "@chakra-ui/layout";
import { VFC } from "react";

export const Login: VFC = () => {
    return (
        <>
            <Flex align="center" justify="center" h="100vh">
                <Box backgroundColor="gray.100" borderRadius="10px" boxShadow="md" textAlign="center" w="25%" p={4}>
                    <Heading>Login</Heading>
                    <Box mt={6} p={4}>
                        <Stack spacing={4}>
                            <Input border="1px" borderColor="gray.300" placeholder="E-Mail" />
                            <Input border="1px" borderColor="gray.300" placeholder="Password" />
                            <Button backgroundColor="teal.200" _hover={{opacity: "0.7"}}>ログイン</Button>
                        </Stack>
                        <Box m={4}>
                            <Link>ユーザー登録</Link>
                        </Box>
                    </Box>
                </Box>
            </Flex>
        </>
    )
}
