import { Button } from "@chakra-ui/button";
import { useDisclosure } from "@chakra-ui/hooks";
import { Input } from "@chakra-ui/input";
import { Box, Flex, Heading, Link, Stack } from "@chakra-ui/layout";
import { ChangeEvent, useState, VFC } from "react";
import { useCurrentLoginUser } from "../../hooks/useCurrentLoginUser";
import { useLoginUser } from "../../hooks/useLoginUser";
import { SignupModal } from "../molecules/SignupModal";

export const Login: VFC = () => {
    const {onOpen, isOpen, onClose} = useDisclosure();
    const {login} = useLoginUser();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const onChangeLoginEmail = (event: ChangeEvent<HTMLInputElement>) => setEmail(event.target.value);
    const onChangeLoginPassword = (event: ChangeEvent<HTMLInputElement>) => setPassword(event.target.value);
    const onClickLogin = () => {
        login(email, password);
        setEmail("");
        setPassword("");
    };
    const {loginUser} = useCurrentLoginUser();
    console.log(loginUser);

    return (
        <>
            <Flex align="center" justify="center" h="100vh">
                <Box backgroundColor="gray.100" borderRadius="10px" boxShadow="md" textAlign="center" p={4}>
                    <Heading>Login</Heading>
                    <Box mt={6} p={4}>
                        <Stack spacing={4}>
                            <Input border="1px" borderColor="gray.300" placeholder="E-Mail" value={email} onChange={onChangeLoginEmail}/>
                            <Input type="password" border="1px" borderColor="gray.300" placeholder="Password" value={password} onChange={onChangeLoginPassword}/>
                        </Stack>
                        <Box mt={6}>
                            <Button backgroundColor="teal.400" _hover={{opacity: "0.7"}} w="100%" disabled={email === "" || password === ""} onClick={onClickLogin}>ログイン</Button>
                        </Box>
                        <Box mt={4}>
                            <Link fontWeight="bold" color="gray.500" onClick={onOpen}>ユーザー登録</Link>
                        </Box>
                    </Box>
                </Box>
            </Flex>
            <SignupModal onClose={onClose} isOpen={isOpen}/>
        </>
    );
};
