import { Button } from "@chakra-ui/button";
import { useDisclosure } from "@chakra-ui/hooks";
import { Image } from "@chakra-ui/image";
import { Input } from "@chakra-ui/input";
import { Box, Flex, Heading, Stack, Text } from "@chakra-ui/layout";
import { useEffect, VFC } from "react";
import { useGetMyTweets } from "../../hooks/useGetMyTweets";
import { FaRegThumbsUp, FaRegCommentAlt } from "react-icons/fa";

export const Home: VFC = () => {
    const {onOpen, onClose, isOpen} = useDisclosure();
    const {getMyTweetState, myTweets, getMyTweets} = useGetMyTweets();

    useEffect(() => {
        getMyTweets();
    }, []);

    return (
        <>
            <Box backgroundColor="teal.400" display={{base: "none", sm: "block"}} position="fixed" ml={60} w="100%">
                <Box ml={2}>
                    <Heading fontSize="3xl" p={2}>Home</Heading>
                </Box>
            </Box>
            <Box ml={{sm: "60"}}>
                <Box w={{base: "80%", sm: "60%"}} ml={{base: "8", sm: "16"}} mt={24}>
                    <Input size="lg" placeholder="今どうしている？"/>
                    <Box textAlign="right">
                        <Button backgroundColor="teal.300" borderRadius="999px" mt={4}>ツイートする</Button>
                    </Box>
                </Box>
                <Box display="block" ml={12} mt={14}>
                    <Stack spacing={6}>
                        {myTweets.map((myTweet) => (
                            <Flex>
                                <Box>
                                    <Image boxSize="50px" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                                </Box>
                                <Box w="100%">
                                    <Text fontWeight="bold">{myTweet.username}</Text>
                                    <Text>{myTweet.time}</Text>
                                    <Text>{myTweet.tweet}</Text>
                                    <Flex>
                                        <Flex>
                                            <FaRegThumbsUp size={18}/>
                                            <Text>{myTweet.tweetLikeCount}</Text>
                                        </Flex>
                                        <Flex>
                                            <FaRegCommentAlt size={18}/>
                                            <Text>{myTweet.tweetCommentCount}</Text>
                                        </Flex>
                                    </Flex>
                                </Box>
                            </Flex>
                        ))}
                    </Stack>
                </Box>
            </Box>
        </>
    );
};
