import { Flex, Stack } from "@chakra-ui/react";
import { Box, Heading, Link, Spacer } from "@chakra-ui/layout";
import { Image } from "@chakra-ui/image";
import { useDisclosure } from "@chakra-ui/hooks";
import { VFC } from "react";
import { MenuIconButton } from "../atoms/MenuIconButton";
import { MenuDrawer } from "../molecules/MenuDrawer";
import { useHistory } from "react-router";
import { ImExit } from "react-icons/im";
import { useCurrentLoginUser } from "../../hooks/useCurrentLoginUser";
import { FaWrench, FaHome, FaRegHandshake } from "react-icons/fa";

export const Header: VFC = () => {
    const history = useHistory();
    const {onOpen, onClose, isOpen} = useDisclosure();
    const {loginUser} = useCurrentLoginUser();

    const onClickGoHome = () => history.push("/home");
    const onClickGoTweet = () => history.push("/home/tweet");
    const onClickGoSetting = () => history.push("/home/setting");
    const onClickGoLogout = () => history.push("/logout");

    return (
        <>
            <Flex align="center" p={2} backgroundColor="teal.200" display={{base: "flex", sm: "none"}}>
                    <MenuIconButton onOpen={onOpen}/>
                    <Box ml={4}>
                        <Heading>Home</Heading>
                    </Box>
                <Spacer />
                <Box onClick={onClickGoLogout}>
                    <ImExit size={30} />
                </Box>
            </Flex>
            <MenuDrawer
                onClose={onClose}
                isOpen={isOpen}
                onClickGoHome={onClickGoHome}
                onClickGoTweet={onClickGoTweet}
                onClickGoSetting={onClickGoSetting}
            />

            <Flex display={{base: "none", sm: "flex"}} mr={6}>
                <Box p={4} px={6} backgroundColor="teal.200" height="100vh">
                    <Box m={4}>
                        <Image boxSize="150" borderRadius="full" src="https://source.unsplash.com/random" alt=""/>
                        <Box mt={4}>
                            <Link fontSize="xl" fontWeight="bold">Text User</Link>
                        </Box>
                    </Box>
                    <Stack spacing={4} mt={12} pl={4} fontSize="xl" fontWeight="bold">
                        <Flex align="center">
                            <FaHome size={24} />
                            <Link onClick={onClickGoHome} ml={1}> Home</Link>
                        </Flex>
                        <Flex align="center">
                            <FaRegHandshake size={24} />
                            <Link onClick={onClickGoTweet} ml={1}>Tweet</Link>
                        </Flex>
                        <Flex align="center">
                            <FaWrench size={24}/> 
                            <Link onClick={onClickGoSetting} ml={1}>Setting</Link>
                        </Flex>
                        <Flex align="center">
                            <ImExit size={24}/> 
                            <Link onClick={onClickGoLogout} ml={1}>Logout</Link>
                        </Flex>
                    </Stack>
                </Box>
            </Flex>
        </>
    );
};
