import { Button } from "@chakra-ui/button";
import { Input } from "@chakra-ui/input";
import { Box, Heading } from "@chakra-ui/layout";
import { VFC } from "react";

export const Home: VFC = () => {

    return (
        <>
            <Box backgroundColor="teal.400" display={{base: "none", sm: "block"}} ml={60}>
                <Box ml={2}>
                    <Heading fontSize="3xl" p={2}>Home</Heading>
                </Box>
            </Box>
            <Box ml={{sm: "60"}}>
                <Box w={{base: "80%", sm: "60%"}} ml={6}>
                    <Input size="2xl"/>
                    <Button>ツイートする</Button>
                </Box>
            </Box>
        </>
    );
};
