import axios from "axios";
import { useState } from "react";
import { useCurrentLoginUser } from "./useCurrentLoginUser";
import { useMessage } from "./useMessage";
import { FollowType } from "../types/followType";
import { FollowListType } from "../types/followListType";

export const useFollow = () => {
    const {showMessage} = useMessage();
    const [followState, setFollowState] = useState(false);
    const {loginUser, setLoginUser} = useCurrentLoginUser();
    const getMyFollow = () => {
        axios.get<FollowListType>(`http://localhost:8080/api/login/getMyFollow/${loginUser.email}`, {headers: {Authorization: loginUser.token as string}})
        .then((res) => {
            console.log(res.data);
            setLoginUser({...loginUser, follow: res.data.followResource, follower: res.data.followerResource});
        })
    };
    const insertFollow = (followemail?: string) => {
        setFollowState(true);
        axios.post("http://localhost:8080/api/home/insertFollow", {email: loginUser.email, followemail: followemail}, {headers: {Authorization: loginUser.token as string}})
        .then((res) => {
            showMessage({title: res.data as string, status: "success"});
            setFollowState(false);
        })
        .catch((res) => {
            showMessage({title: res.data as string, status: "error"});
            setFollowState(false);
        });
    };
    const deleteFollow = (followemail?: string) => {
        setFollowState(true);
        axios.delete(`http://localhost:8080/api/home/deleteFollow/${loginUser.email}/${followemail}`, {headers: {Authorization: loginUser.token as string}})
        .then((res) => {
            showMessage({title: res.data as string, status: "success"});
            setFollowState(false);
        })
        .catch((res) => {
            showMessage({title: res.data as string, status: "error"});
            setFollowState(false);
        });
    };

    return {followState, getMyFollow, insertFollow, deleteFollow};
};
