import { Image } from "@chakra-ui/image";
import { Box, Flex, Heading, Stack, Text } from "@chakra-ui/layout";
import { useEffect, VFC } from "react";
import { useCurrentLoginUser } from "../../hooks/useCurrentLoginUser";
import { useGetAllTweets } from "../../hooks/useGetAllTweets";
import { TweetType } from "../../types/tweetType";

export const Tweet: VFC = () => {
    const {getAllTweetsState, allTweets, getAllTweets} = useGetAllTweets();
    const {loginUser} = useCurrentLoginUser();

    const isMyTweet = (allTweet?: TweetType) => {
        let isMyTweet = false;
        if(loginUser.email === allTweet?.email){
            isMyTweet = true;
        }
        return isMyTweet;
    };

    useEffect(() => {
        getAllTweets();
    }, []);

    return (
        <>
            <Box backgroundColor="teal.400" display={{base: "none", sm: "block"}} ml={60}>
                <Box ml={2}>
                    <Heading fontSize="3xl" p={2}>Tweet</Heading>
                </Box>
                <Box>
                    <Stack>
                        {allTweets.map((allTweet) => (
                            !isMyTweet(allTweet) &&
                                <Stack>
                                    <Flex>
                                        <Box>
                                            <Image boxSize="50px" borderRadius="full" src="https://source.unsplash.com/random" alt="" />
                                        </Box>
                                        <Box>
                                            <Text fontWeight="bold">{allTweet.username}</Text>
                                            <Text>{allTweet.time}</Text>
                                            <Text>{allTweet.tweet}</Text>
                                        </Box>
                                    </Flex>
                                </Stack>
                        ))}
                    </Stack>
                </Box>
            </Box>
        </>
    );
};
