import axios from "axios";
import { useState } from "react";
import { useCurrentLoginUser } from "./useCurrentLoginUser";
import { useMessage } from "./useMessage";

export const useFollow = () => {
    const {showMessage} = useMessage();
    const [followState, setFollowState] = useState(false);
    const {loginUser, setLoginUser} = useCurrentLoginUser();
    const getMyFriends = (email?: string, token?: string) => {
        axios.get(`http://localhost:8080/api/login/getFollow/${email}`, {headers: {Authorization: token as string}})
        .then((res) => {
            setLoginUser({});
        })
    };
    const insertFollow = (friendemail?: string) => {
        setFollowState(true);
        axios.post("http://localhost:8080/api/home/insertFollow", {email: loginUser.email, friendemail: friendemail}, {headers: {Authorization: loginUser.token as string}})
        .then((res) => {
            showMessage({title: res.data as string, status: "success"});
            setFollowState(false);
        })
        .catch((res) => {
            showMessage({title: res.data as string, status: "error"});
            setFollowState(false);
        });
    };
    const deleteFollow = (friendemail?: string) => {
        setFollowState(true);
        axios.delete(`http://localhost:8080/api/home/deleteFollow/${loginUser.email}/${friendemail}`, {headers: {Authorization: loginUser.token as string}})
        .then((res) => {
            showMessage({title: res.data as string, status: "success"});
            setFollowState(false);
        })
        .catch((res) => {
            showMessage({title: res.data as string, status: "error"});
            setFollowState(false);
        });
    };

    return {followState, insertFollow, deleteFollow};
};
