import axios from "axios";
import { useState } from "react";
import { useCurrentLoginUser } from "./useCurrentLoginUser";
import { useMessage } from "./useMessage";

export const useChangeImage = () => {
    const [changeImageState, setChangeImageState] = useState(false);
    const {loginUser} = useCurrentLoginUser();
    const {showMessage} = useMessage();
    const changeImage = (profileImage?: string) => {
        setChangeImageState(true);
        axios.post(`http://localhost:8080/api/login/changeProfileImage/${loginUser.email}`, {...loginUser, profileImage: "asdfasdf"}, {headers: {Authorization: loginUser.token as string}})
        .then((res) => {
            showMessage({title: res.data as string, status: "success"});
            setChangeImageState(false);
        })
        .catch((res) => {
            showMessage({title: res.data as string, status: "success"});
            setChangeImageState(false);
        });
    };

    return {changeImageState, changeImage};
};
