import { Box, Flex, Stack, Text } from "@chakra-ui/layout";
import { Drawer, DrawerBody, DrawerCloseButton, DrawerContent, DrawerOverlay } from "@chakra-ui/modal";
import { VFC } from "react";
import { FaWrench, FaHome, FaWeixin } from "react-icons/fa";
import { useCurrentLoginUser } from "../../hooks/useCurrentLoginUser";
import { ProfileImage } from "../atoms/ProfileImage";

type Props = {
    onClose: () => void;
    isOpen: boolean;
    onClickGoHome: () => void;
    onClickGoTweet: () => void;
    onClickGoSetting: () => void;
}

export const MenuDrawer: VFC<Props> = (props) => {
    const {onClose, isOpen, onClickGoHome, onClickGoTweet, onClickGoSetting} = props;
    const {loginUser} = useCurrentLoginUser();

    return (
        <Drawer placement="left" onClose={onClose} isOpen={isOpen} variant="outline">
            <DrawerOverlay>
                <DrawerContent>
                    <DrawerBody>
                        <Box p={4} pb={8} mt={6} borderBottom="4px" borderColor="gray.300">
                            <ProfileImage size={"120px"} image={loginUser.profileImage}/>
                            <Box mt={4} ml={2}>
                                <Text fontSize="2xl" fontWeight="bold">{loginUser.username}</Text>
                            </Box>
                        </Box>
                        <Box mt={6} p={4} fontSize="xl" fontWeight="bold">
                            <Stack>
                                <Flex align="center" onClick={() => {
                                    onClickGoHome();
                                    onClose();
                                }}>
                                    <FaHome size={20} />
                                    <Text ml={2}>Home</Text>
                                </Flex>
                                <Flex align="center" onClick={() => {
                                    onClickGoTweet();
                                    onClose();
                                }}>
                                    <FaWeixin size={20} />
                                    <Text ml={2}>Tweet</Text>
                                </Flex>
                                <Flex align="center" onClick={() => {
                                    onClickGoSetting();
                                    onClose();
                                }}>
                                    <FaWrench size={20}/>
                                    <Text ml={2}>Setting</Text>
                                </Flex>
                            </Stack>
                        </Box>
                    </DrawerBody>
                    <DrawerCloseButton />
                </DrawerContent>
            </DrawerOverlay>
        </Drawer>
    );
};
