import { Button } from "@chakra-ui/button";
import { Image } from "@chakra-ui/image";
import { Input } from "@chakra-ui/input";
import { Box, Flex, Text } from "@chakra-ui/layout";
import { Modal, ModalBody, ModalCloseButton, ModalContent, ModalHeader, ModalOverlay } from "@chakra-ui/modal";
import { ChangeEvent, useRef, useState, VFC } from "react";
import { useChangeImage } from "../../hooks/useChangeImage";

type Props = {
    onClose: () => void;
    isOpen: boolean;
};

export const ImageUploadModal: VFC<Props> = (props) => {
    const {onClose, isOpen} = props;
    const [profileIcon, setProfileIcon] = useState<string>();

    const getBase64 = (file: File) => {
        let reader = new FileReader();
        reader.readAsDataURL(file);
        reader.onload = () => {
            console.log(reader);
            let baseURL = reader.result as string;
            setProfileIcon(baseURL);
        };
    }
    
    const handleFileInputChange = (event: ChangeEvent<HTMLInputElement>) => {
        let image = event.target.files![0];
        getBase64(image);
    }

    return (
        <Modal onClose={onClose} isOpen={isOpen}>
            <ModalOverlay>
                <ModalContent>
                    <ModalHeader>画像を変更する</ModalHeader>
                    <ModalBody>
                        <Input type="file" accept="image/*" border="none" onChange={handleFileInputChange}/>
                        <Button border="1px" size="lg">リセット</Button>
                        <Box w="100%" p={4}>
                            <Image src={profileIcon}/>
                        </Box>
                    </ModalBody>
                    <ModalCloseButton />
                </ModalContent>
            </ModalOverlay>
        </Modal>
    );
};
