import { Modal, ModalBody, ModalCloseButton, ModalContent, ModalHeader, ModalOverlay } from "@chakra-ui/modal";
import { Box, Stack, Input, Button, Text } from "@chakra-ui/react";
import { ChangeEvent, useState, VFC } from "react";
import { useCreateUser } from "../../hooks/useCreateUser";

type Props = {
    onClose: () => void;
    isOpen: boolean;
};

export const SignupModal: VFC<Props> = (props) => {
    const {onClose, isOpen} = props;
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [username, setUsername] = useState("");
    const {createUser} = useCreateUser();

    const onChangeSignupEmail = (event: ChangeEvent<HTMLInputElement>) => setEmail(event.target.value);
    const onChangeSignupPassword = (event: ChangeEvent<HTMLInputElement>) => setPassword(event.target.value);
    const onChangeSignupUsername = (event: ChangeEvent<HTMLInputElement>) => setUsername(event.target.value);
    const onClickSignupUser = () => {
        createUser(email, password, username);
        setEmail("");
        setPassword("");
        setUsername("");
    }

    return (
        <Modal onClose={onClose} isOpen={isOpen}>
            <ModalOverlay>
                <ModalContent>
                    <ModalHeader>ユーザー登録</ModalHeader>
                    <ModalBody>
                        <Box mb={2} px={6} py={4}>
                            <Stack spacing={3}>
                                <Box>
                                    <Text p={2} fontWeight="bold">E-Mail</Text>
                                    <Input borderColor="gray.300" value={email} onChange={onChangeSignupEmail}/>
                                </Box>
                                <Box>
                                    <Text p={2} fontWeight="bold">Password</Text>
                                    <Input type="password" borderColor="gray.300" value={password} onChange={onChangeSignupPassword}/>
                                </Box>
                                <Box>
                                    <Text p={2} fontWeight="bold">ユーザー名</Text>
                                    <Input borderColor="gray.300" value={username} onChange={onChangeSignupUsername}/>
                                </Box>
                            </Stack>
                            <Box mt={8} mb={2}>
                                <Button
                                    backgroundColor="teal.400"
                                    _hover={{opacity: "0.7"}}
                                    w="100%"
                                    disabled={email === "" || password === "" || username === ""}
                                    onClick={() => {onClickSignupUser(); onClose();}}
                                >
                                    登録
                                </Button>
                            </Box>
                        </Box>
                    </ModalBody>
                    <ModalCloseButton />
                </ModalContent>
            </ModalOverlay>
        </Modal>
    );
};
